(function () {
    const o = document.createElement("link").relList;
    if (o && o.supports && o.supports("modulepreload")) return;
    for (const e of document.querySelectorAll('link[rel="modulepreload"]')) r(e);
    new MutationObserver((e) => {
        for (const s of e)
            if (s.type === "childList")
                for (const d of s.addedNodes) d.tagName === "LINK" && d.rel === "modulepreload" && r(d);
    }).observe(document, { childList: !0, subtree: !0 });

    function n(e) {
        const s = {};
        e.integrity && (s.integrity = e.integrity);
        e.referrerPolicy && (s.referrerPolicy = e.referrerPolicy);
        s.credentials =
            e.crossOrigin === "use-credentials" ? "include" : e.crossOrigin === "anonymous" ? "omit" : "same-origin";
        return s;
    }

    function r(e) {
        if (e.ep) return;
        e.ep = !0;
        const s = n(e);
        fetch(e.href, s);
    }
})();

